.testimonial .p,
.testimonial-content .p {
	margin:0;
}

@-moz-keyframes
fade-it {
	0% { opacity:0 }
	100% { opacity:1 }
}

@-webkit-keyframes
fade-it {
	0% { opacity:0 }
	100% { opacity:1 }
}

@keyframes
fade-it {
	0% { opacity:0 }
	100% { opacity:1 }
}

.fade-in-rotate {
  animation-name: fade-it;
  animation-duration: 1s;
  animation-fill-mode: forwards;
  -webkit-animation-name: fade-it;
  -webkit-animation-duration: 1s;
  -webkit-animation-fill-mode: forwards
}

.rotate.rotate-block { display: inline-block }

.rotate.rotate-hide {
  display: none;
  opacity: 0
}

.rotate {
  display: inline-block;
  visibility: visible;
  color: #F6AD55;
}

@media (max-width: 768px) {
	.rotate {
		display: block !important;
	}
}

.footer-message { display: none; }

javascript:
frappe.ready(() => {
	$('.container').animatedHeadline({
		animationType: "slide",
		animationDelay: 2500,
		barAnimationDelay: 3800,
		barWaiting: 800,
		lettersDelay: 50,
		typeLettersDelay: 150,
		selectionDuration: 500,
		typeAnimationDelay: 1300,
		revealDuration: 600,
		revealAnimationDelay: 1500
	  });
})
let words = (function () {
	let words = [
		"SAP",
		"Oracle",
		"Salesforce",
		"NetSuite",
		"MS Dynamics",
		"Zoho",
		"Freshworks"
	],
		el = document.querySelector('.rotate'),
		currentIndex = 0,
		currentWord = '',
		duration = 2000;

	var _clear = function () {
		setTimeout(function () {
			el.className = 'rotate';
		}, duration / 4);
	};

	var _toggleWord = function (duration) {
		setInterval(function () {
			//Generate new current word
			currentIndex = currentIndex + 1
			currentWord = words[currentIndex %words.length];

			//Swap new value
			el.innerHTML = currentWord;

			//Clear class styles
			_clear();

			//Fade in word
			el.classList.add(
				'rotate-block',
				'fade-in-rotate'
			);

		}, duration);
	};

	var _init = function () {
		_toggleWord(duration);
	};

	//Public API
	return {
		init: function () {
			_init();
		}
	};
})();

words.init();

